/**
 * The response object
 *
 * The complete response object that was returned by the Responses API.
 */
export interface ResponseResource {
  /**
   * Whether this request was run in the background.
   */
  background: boolean
  completed_at: null | number
  /**
   * The Unix timestamp (in seconds) for when the response was created.
   */
  created_at: number
  error: (Error & unknown) | null
  /**
   * The frequency penalty that was used to penalize new tokens based on their frequency in the text so far.
   */
  frequency_penalty: number
  /**
   * The unique ID of the response that was created.
   */
  id: string
  incomplete_details: (IncompleteDetails & unknown) | null
  instructions: null | string
  max_output_tokens: null | number
  max_tool_calls: null | number
  /**
   * Developer-defined metadata that was associated with the response.
   */
  metadata: unknown
  /**
   * The model that generated this response.
   */
  model: string
  /**
   * The object type, which was always `response`.
   */
  object: 'response'
  /**
   * The output items that were generated by the model.
   */
  output: Array<ItemField>
  /**
   * Whether the model was allowed to call multiple tools in parallel.
   */
  parallel_tool_calls: boolean
  /**
   * The presence penalty that was used to penalize new tokens based on whether they appear in the text so far.
   */
  presence_penalty: number
  previous_response_id: null | string
  prompt_cache_key: null | string
  reasoning: null | (Reasoning & unknown)
  safety_identifier: null | string
  /**
   * The service tier that was used for this response.
   */
  service_tier: string
  /**
   * The status that was set for the response.
   */
  status: string
  /**
   * Whether this response was stored so it can be retrieved later.
   */
  store: boolean
  /**
   * The sampling temperature that was used for this response.
   */
  temperature: number
  text: TextField & unknown
  tool_choice: AllowedToolChoice | FunctionToolChoice | ToolChoiceValueEnum
  /**
   * The tools that were available to the model during response generation.
   */
  tools: Array<Tool>
  /**
   * The number of most likely tokens that were returned at each position, along with their log probabilities.
   */
  top_logprobs: number
  /**
   * The nucleus sampling parameter that was used for this response.
   */
  top_p: number
  truncation: TruncationEnum & unknown
  usage: null | (unknown & Usage)
}

export type ResponsesToolParam = FunctionToolParam

export type ServiceTierEnum = 'auto' | 'default' | 'flex' | 'priority'

export interface SpecificFunctionParam {
  /**
   * The name of the function tool to call.
   */
  name: string
  /**
   * The tool to call. Always `function`.
   */
  type: 'function'
}

export type SpecificToolChoiceParam = SpecificFunctionParam

/**
 * Options that control streamed response behavior.
 */
export interface StreamOptionsParam {
  /**
   * Whether to obfuscate sensitive information in streamed output. Defaults to `true`.
   */
  include_obfuscation?: boolean
}

/**
 * Summary text
 *
 * A summary text from the model.
 */
export interface SummaryTextContent {
  /**
   * A summary of the reasoning output from the model so far.
   */
  text: string
  /**
   * The type of the object. Always `summary_text`.
   */
  type: 'summary_text'
}

export interface SystemMessageItemParam {
  /**
   * The message content, as an array of content parts.
   */
  content: Array<InputTextContentParam> | string
  id?: null | string
  /**
   * The message role. Always `system`.
   */
  role: 'system'
  status?: null | string
  /**
   * The item type. Always `message`.
   */
  type: 'message'
}

/**
 * Text Content
 *
 * A text content.
 */
export interface TextContent {
  text: string
  type: 'text'
}

export interface TextField {
  format: JsonObjectResponseFormat | JsonSchemaResponseFormat | TextResponseFormat
  verbosity?: VerbosityEnum
}

export type TextFormatParam = JsonSchemaResponseFormatParam | TextResponseFormat

export interface TextParam {
  /**
   * The format configuration for text output.
   */
  format?: null | TextFormatParam
  verbosity?: unknown & VerbosityEnum
}

export interface TextResponseFormat {
  type: 'text'
}

/**
 * A tool that can be used to generate a response.
 */
export type Tool = FunctionTool

/**
 * Controls which tool the model should use, if any.
 */
export type ToolChoiceParam = AllowedToolsParam | SpecificToolChoiceParam | ToolChoiceValueEnum

export type ToolChoiceValueEnum = 'auto' | 'none' | 'required'

/**
 * Top log probability
 *
 * The top log probability of a token.
 */
export interface TopLogProb {
  bytes: Array<number>
  logprob: number
  token: string
}

export type TruncationEnum = 'auto' | 'disabled'

/**
 * URL citation
 *
 * A citation for a web resource used to generate a model response.
 */
export interface UrlCitationBody {
  /**
   * The index of the last character of the URL citation in the message.
   */
  end_index: number
  /**
   * The index of the first character of the URL citation in the message.
   */
  start_index: number
  /**
   * The title of the web resource.
   */
  title: string
  /**
   * The type of the URL citation. Always `url_citation`.
   */
  type: 'url_citation'
  /**
   * The URL of the web resource.
   */
  url: string
}

export interface UrlCitationParam {
  /**
   * The index of the last character of the citation in the message.
   */
  end_index: number
  /**
   * The index of the first character of the citation in the message.
   */
  start_index: number
  /**
   * The title of the cited resource.
   */
  title: string
  /**
   * The citation type. Always `url_citation`.
   */
  type: 'url_citation'
  /**
   * The URL of the cited resource.
   */
  url: string
}

/**
 * Usage
 *
 * Token usage statistics that were recorded for the response.
 */
export interface Usage {
  /**
   * The number of input tokens that were used to generate the response.
   */
  input_tokens: number
  input_tokens_details: InputTokensDetails & unknown
  /**
   * The number of output tokens that were generated by the model.
   */
  output_tokens: number
  output_tokens_details: OutputTokensDetails & unknown
  /**
   * The total number of tokens that were used.
   */
  total_tokens: number
}

export interface UserMessageItemParam {
  /**
   * The message content, as an array of content parts.
   */
  content: Array<InputFileContentParam | InputImageContentParamAutoParam | InputTextContentParam> | string
  id?: null | string
  /**
   * The message role. Always `user`.
   */
  role: 'user'
  status?: null | string
  /**
   * The item type. Always `message`.
   */
  type: 'message'
}

export type VerbosityEnum = 'high' | 'low' | 'medium'
